```{.r .cell-code}
library(tidyverse)
library(gt)
library(gtExtras)
```
:::

::: {.cell}

:::

::: {.cell}

```{.r .cell-code}
set.seed(1)
d <- crossing(
  sex = c("male", "female"),
  age = seq(30, 60, length.out = 5),
  group = c("case", "control")
) |> 
  mutate(
    y = rnorm(n()) + (sex == "male") - 0.5 * (age / 10) + (group == "case")
  )

m <- lm(y ~ age + sex + group, data = d) |> summary()
```
:::

::: {.cell}

```{.r .cell-code}
tab <- m |> 
  tidy(conf.int = TRUE) |> 
  mutate(
    plot_col = estimate
  ) |> 
  gt() |> 
  cols_label(
    term = "Term",
    estimate = "Mean [95% CI]",
    p.value = "p-value",
    plot_col = ""
  ) |> 
  cols_hide(
    std.error
  ) |> 
  cols_move(
    p.value,
    after = plot_col
  ) |> 
  cols_move(
    plot_col,
    after = term
  ) |> 
  cols_merge(
    columns = c(estimate, conf.low, conf.high),
    pattern = "{1} [{2}, {3}]"
  ) |> 
  gt_plt_conf_int(
    plot_col, 
    ci_columns = c(conf.low, conf.high), 
    ref_line = 0, 
    text_size = 0
  ) |> 
  fmt_number(
    estimate:conf.high,
    decimals = 2
  ) |> 
  fmt_number(
    p.value,
    decimals = 3
  ) |> 
  text_transform(
    function(x) {
      if_else(
        str_detect(x, "[1-9]"),
        paste0("p = ", x),
        "p < 0.001"
      )
    },
    locations = cells_body(
      columns = p.value
    )
  )

tab |> 
  gtsave("featured.png")
```
